ini_set('display_errors', 1);
ini_set('display_startup_errors', 1);
error_reporting(E_ALL);
	
$input = ".
.
.
.
.";

$postition["x"] = 2;
$postition["y"] = 2;

function showpos ($x, $y)
{
	$number = ($x-1) * 3;
	$number = $number + $y;
	return $number;
}

echo "<h2>Starting process on digit " . showpos($postition["x"],$postition["y"]) . "</h2>";

echo "<h2>The string is " . strlen($input) . " characters long</h2>";

for ($i = 1; $i <= strlen($input); $i++) 
	{
	$thischar = substr($input,$i,1);
	if ($thischar == ".")
		{ echo "<h1>Found a digit!</h1>";
		  echo "<p>The digit is " . showpos($postition["x"],$postition["y"]) . "</p>"; 
	    }
		else
		{
			if ($thischar == "U")
			{
				$postition["x"] = $postition["x"]-1;
			}
			if ($thischar == "D")
			{
				$postition["x"] = $postition["x"]+1;
			}
			if ($thischar == "L")
			{
				$postition["y"] = $postition["y"]-1;
			}
			if ($thischar == "R")
			{
				$postition["y"] = $postition["y"]+1;
			}
		}
		
		if ($postition["y"] < 1) { $postition["y"] = 1; }		
		if ($postition["x"] < 1) { $postition["x"] = 1; }		
		if ($postition["y"] > 3) { $postition["y"] = 3; }		
		if ($postition["x"] > 3) { $postition["x"] = 3; }		

}	


?>